import PokeCard from "../PokeCard";

// ---------- Finds the final 6 pokemons and render them ---------- //
function PokemonResults (props) {
    
    let chosenPokemon = [];
    const chosenPokemonControl = [];
    let indexFinalScore = 0;

    // console.log('evolutions', props.evolutions)
    
    // ----- While the number of pokemon is smaller than 6, do the loop -----
    while (chosenPokemon.length < 6 ) {
        // console.log('Entrou!');
        // console.log('indexFinalScore:', indexFinalScore);

        const filteredType_Habitat = props.pokemonAttributes.filter(
            attr => attr.type1 === props.finalScore[indexFinalScore].type_habitat || 
            attr.type2 === props.finalScore[indexFinalScore].type_habitat || 
            attr.habitat === props.finalScore[indexFinalScore].type_habitat
        ).filter(pokeColor => pokeColor.color === props.formAnswers.color || pokeColor.color === 'yellow');

        // console.log(filteredType_Habitat)

        //let filteredColor = filteredType_Habitat.filter(pokeColor => pokeColor.color === props.formAnswers.color || pokeColor.color === 'yellow');
        // colocar para shuffle cores tbm


        // console.log('pokemons filtrados pela cor:', filteredColor)
        let filteredColorCopy = filteredType_Habitat.map((x) => x);
        // let filteredColorCopy = filteredColor.map((x) => x);
        
        const shuffle = (myArray) => { 
            for (let i = myArray.length - 1; i > 0; i--) { 
                const j = Math.floor(Math.random() * (i + 1)); 
                [myArray[i], myArray[j]] = [myArray[j], myArray[i]]; 
            } 
            return myArray; 
        }; 

        const filteredColorShuffled = shuffle(filteredColorCopy);

        //console.log('Shuffle:', filteredColorShuffled);

        for (let pokemonIndex in filteredColorShuffled) {
            // pokemonIndex é um index ou um nome?
            let currentPokemon = filteredColorShuffled[pokemonIndex].name;
            let newIndex = props.evolutions.findIndex(group => group.first === currentPokemon || group.second === currentPokemon || group.third === currentPokemon );
            //console.log('índice da evolução', newIndex);

            if (chosenPokemonControl.includes(props.evolutions[newIndex].first || props.evolutions[newIndex].second || props.evolutions[newIndex].third)) {
                //console.log('Já tem uma evolução!')
                continue
            }

            if (chosenPokemon.length >= 6) {
                break
            }
            
            // !!!!! Ajeitar condicional para jolteon, vaporeon, etc.
            // chosenPokemon.push(filteredColorShuffled[pokemonIndex])
            chosenPokemon = [...chosenPokemon, filteredColorShuffled[pokemonIndex]]
            // chosenPokemon.push(pokemonIndex)
            chosenPokemonControl.push(filteredColorShuffled[pokemonIndex].name)
            // console.log('Array de controle:', chosenPokemonControl)
            // console.log('Lista final de pokemon:', chosenPokemon)

            // console.log('Nome:' , filteredColor[pokemonIndex]);
            // console.log('Índice:', newIndex);
            // console.log('Lista final:', chosenPokemon);
        }
        indexFinalScore++
    }

    console.log('Lista final final de pokemon:', chosenPokemon);
        


    // console.log('filteredType_Habitat:', filteredType_Habitat);
    // console.log('filteredColor:', filteredColor)
	return (
		<section>
                <div className="card-container">
                {chosenPokemon.map(pokeAttributes => {
                return <PokeCard key={pokeAttributes.name} name={pokeAttributes.name} image={pokeAttributes.image}/>
                })}
                </div>
		</section>
	)
}

export default PokemonResults;
