import axios from "axios";
import { useEffect, useState } from "react";
import PokeCard from "../components/PokeCard";

function Home () {

    useEffect(() => {
        getPokemonList();
    }, []) // Para evitar uma re-renderização a cada mudança

    const [pokemons, setPokemons] = useState([]);

    function getPokemonList() {
        // axios.get('https://pokeapi.co/api/v2/pokemon?limit=50')
        // .then(getPokemonList => setPokemons(getPokemonList.data.results))
        // .catch(error => console.log('An error with the following code occured:', error.code));
        
        let endpoints = [];
        
        for (let i = 1; i <= 150; i++) {
            endpoints.push(`https://pokeapi.co/api/v2/pokemon/${i}`);
        }

        let pokeResults = axios.all(endpoints.map(endpoint => 
            axios.get(endpoint)
            .then(result => console.log(setPokemons(result.data)))
            .catch(error => console.log('An error with the following code occured:', error.code))
        ))

        return pokeResults

        // console.log(pokeResults)

    }

    // console.log(pokemons);

    // pokemons.map(pokemon => {
    //     return console.log(pokemon.name)
    // })

	return (
        <div>
            {pokemons.map(pokemon => {
                return <PokeCard name={pokemon.name} />
            })}
        </div>
    );
}

export default Home;
