import { useEffect, useState } from 'react';
import MoreInfoLink from '../MoreInfoLink/MoreInfoLink';
import './WelcomeMessage.css';
import { Link } from 'react-router-dom';

function WelcomeMessage () {

    const [isMoreInfo, setIsMoreInfo] = useState(false);

    // useEffect (() => {

    // },[isMoreInfo]);

    const toggleMoreInfo = () => {
        isMoreInfo ? setIsMoreInfo(false) : setIsMoreInfo(true);
        console.log(isMoreInfo)
    };

	return (
        <>
            <MoreInfoLink toggleMoreInfo={toggleMoreInfo()} />
            <h1 className="form-title">
                Find out your six
                <img src="images/logo-pokemon.png" alt="Pokémon logo" />
            </h1>
            <div className="container">
                    <p>Want to find out which Pokémon you'd carry with you if you're on your adventure to become a Pokémon Master? <br/><br/>Hit the button and let's have fun!</p>
                    
                    <Link to="/pokeBuzz"><button className='home-button'>Start quizz!</button></Link>
                    
                    <img src="/images/pikachu.png" alt="" />
            </div>
        </>
    );
}



export default WelcomeMessage;
