import { useState } from "react";
import { findPokemon } from "../../utils/findPokemon";
import PokeCard from "../PokeCard";
import Pokedex from "../Pokedex";
import "./PokemonResults.css";

// ---------- Finds the final 6 pokemons and render them ---------- //
function PokemonResults (props) {

    const [chosenPokemon] = useState(() =>findPokemon(props));
    console.log(chosenPokemon)
    const [index, setIndex] = useState(0);

    const changePokemon = (e) => {
        const command = e.target.innerText;
        
        if (command === 'Next') {
            setIndex((prevIndex) => (prevIndex + 1) % chosenPokemon.length);
        }

        if (command === 'Prev') {
            setIndex((prevIndex) => (prevIndex - 1) % chosenPokemon.length);
        }
    }

    const currentPokemon = chosenPokemon[index];
    console.log(currentPokemon);

	return (
		<section>
            <div className="card-container">
                    <Pokedex changePokemon={changePokemon} leftScreenContent={<img className='pokemon-pokedex' 
                    src={currentPokemon.image}
                    // 
                    />
                    }/>
                 {/* return <PokeCard key={attr.name} name={attr.name} image={attr.image}/> */}
            </div>
		</section>
	)
}

export default PokemonResults;
