import { useState, useEffect } from "react";
import Option from "../Option";

function Question (props) {

    const options = {
        colorOptions: ['Black', 'Blue', 'Brown', 'Gray', 'Green', 'Pink', 'Purple', 'Red', 'Yellow', 'White'],
        transportationOptions: ['Car', 'Airplane', 'Ship', 'Train', 'By foot'],
        settingOptions: ['Countryside', 'Beach', 'Mountain', 'City', 'Cave'],
        naturalPhenomenonOptions: ['Volcano', 'Earthquake', 'Tsunami', 'Hurricane', 'Storm', 'Sand storm', 'Blizzard'],
        activityOptions: ['Pool party', 'Work out', 'Circus', 'Stay in and chill', 'Rally', 'Hiking'],
        chillOptions: ['Gym', 'Cemetery', 'Mall', 'Pool', 'Library', 'Park', 'Sauna'],
        taylorOptions: ['Debut', 'Fearless TV', 'Speak Now TV', 'Red TV', '1989 TV', 'Reputation', 'Lover', 'Folklore', 'Evermore', 'Midnights', 'The Tortured Poets Department']
    }

    const [color, setColor] = useState('');
	const [transportation, setTransportation] = useState('');
	const [setting, setSetting] = useState('');
	const [naturalPhenomenon, setNaturalPhenomenon] = useState('');
	const [activity, setActivity] = useState('');
	const [placeToChill, setPlaceToChill] = useState('');
	const [taylorAlbum, setTaylorAlbum] = useState('');
    const [formData, setFormData] = useState({
        color: '',
        transportation: '',
        setting: '',
        naturalPhenomenon: '',
        activity: '',
        placeToChill: '',
        taylorAlbum: ''
    });
    // let teste = {
    //     color,
    //     transportation,
    //     setting,
    //     naturalPhenomenon,
    //     activity,
    //     placeToChill,
    //     taylorAlbum
    // };

	const getSelectionValue = (e) => {
		// console.log(e.target.value)

		if (e.target.name === 'colorOptions') {
			setColor(e.target.value);
		} else if (e.target.name === 'transportationOptions') {
			setTransportation(e.target.value);
		} else if (e.target.name === 'settingOptions') {
			setSetting(e.target.value);
		} else if (e.target.name === 'naturalPhenomenonOptions') {
			setNaturalPhenomenon(e.target.value);
		} else if (e.target.name === 'activityOptions') {
			setActivity(e.target.value);
		} else if (e.target.name === 'chillOptions') {
			setPlaceToChill(e.target.value);
		} else if (e.target.name === 'taylorOptions') {
			setTaylorAlbum(e.target.value);
		}
        if (color && transportation && setting && naturalPhenomenon && activity && placeToChill && taylorAlbum) {
            console.log('Completo!')
        }
        // setFormData({
        //     color,
        //     transportation,
        //     setting,
        //     naturalPhenomenon,
        //     activity,
        //     placeToChill,
        //     taylorAlbum
        // });
        // console.log(color, transportation, setting, naturalPhenomenon, activity, placeToChill, taylorAlbum);
	}

    useEffect(() => {
        
        const handleStateChange = () => {
            // let teste = [color, transportation, setting, naturalPhenomenon, activity, placeToChill, taylorAlbum];
            setFormData({
                color,
                transportation,
                setting,
                naturalPhenomenon,
                activity,
                placeToChill,
                taylorAlbum
            });
            // console.log('Color:', color, 'Transportation:', transportation, 'Setting:', setting, 'Phenomenon:', naturalPhenomenon, 'Activity:', activity, 'Place:',  placeToChill, 'Taylor:', taylorAlbum)
            console.log(formData)
            // props.testing(teste)
        };
        
        handleStateChange();
    }, [color, transportation, setting, naturalPhenomenon, activity, placeToChill, taylorAlbum]);

	return (
            <fieldset>
                <legend>{props.label}</legend>
                {Object.values(options)[props.index].map(option => 
                    <Option 
                        key={`option-${option.toLowerCase()}`} 
                        id={`option-${option.toLowerCase()}`} 
                        name={Object.keys(options)[props.index]} 
                        value={option.toLowerCase()} 
                        content={option} 
                        getData={getSelectionValue} />
                )}
            </fieldset>
	)
}

export default Question;
