//     taylorOptions: ["Debut", "Fearless TV", "Speak Now TV", "Red TV", "1989 TV", "Reputation", "Lover", "Folklore", "Evermore", "Midnights", "The Tortured Poets Department"]
// }


    // const questionNameMap = {
    //     colorOptions: 'color',
    //     transportationOptions: 'transportation',
    //     settingOptions: 'setting',
    //     naturalPhenomenonOptions: 'naturalPhenomenon',
    //     activityOptions: 'activity',
    //     chillOptions: 'placeToChill',
    //     taylorOptions: 'taylorAlbum'
    // };

    const optionKey = Object.keys(questionOptions)[index];
    // console.log(optionKey)
    const questionName = questionNameMap[optionKey];
    // console.log(questionName)

    const handleChange = e => {
        const selectedOption = e.target.value;
        // console.log('Question name:', questionName)
        // console.log('Opção selecionada:', selectedOption);
        setAnswer(selectedOption);
        getFormAnswers({ [questionName]: selectedOption }); // From Form component
    }

       

	return (
            <fieldset>
                <legend>{label}</legend>
                {questionOptions[optionKey].map(option => 
                    <Option 
                        key={`option-${option.toLowerCase().replace(/\s/g, '')}`} 
                        id={`option-${option.toLowerCase().replace(/\s/g, '')}`} 
                        name={optionKey} 
                        value={option.toLowerCase().replace(/\s/g, '')} 
                        content={option} 
                        getData={handleChange} />
                )}
            </fieldset>
	)
}

export default Question;

