import PokeCard from "../PokeCard";

// ---------- Finds the final 6 pokemons and render them ---------- //
function PokemonResults (props) {
    
    const finalPokemons = [];
    const controlArray = [];
    let controlVariable = 0;

    console.log('finalPokemons início', finalPokemons);

    console.log('evolutions', props.evolutions)
    
    // ----- While the number of pokemon is smaller than 6, do the loop -----
    while (finalPokemons.length < 6 ) {
        // console.log('Entrou!');
        // console.log('controlVariable:', controlVariable);

        const filteredType_Habitat = props.pokemonAttributes.filter(
            attr => attr.type1 === props.pokemonResults[controlVariable].type_habitat || 
            attr.type2 === props.pokemonResults[controlVariable].type_habitat || 
            attr.habitat === props.pokemonResults[controlVariable].type_habitat
        );

        console.log(filteredType_Habitat)

        let filteredColor = filteredType_Habitat.filter(pokeColor => pokeColor.color === props.formAnswers.color || pokeColor.color === 'yellow');

        console.log('pokemons filtrados pela cor:', filteredColor)
        let filteredColorCopy = filteredColor.map((x) => x);

        // if (filteredColor.length === 0) {
        //     for (let i = 1; i <= 6; i++) {
        //         filteredColor = filteredType_Habitat.filter(pokeColor => pokeColor.color === 'yellow');
        //         if (filteredColor.length > 0) {
        //             break
        //         }
        //     }
        // }

        // Colocar para ao invés de escolher a cor preferida, rankear as cores.
        
        const shuffle = (myArray) => { 
            for (let i = myArray.length - 1; i > 0; i--) { 
                const j = Math.floor(Math.random() * (i + 1)); 
                [myArray[i], myArray[j]] = [myArray[j], myArray[i]]; 
            } 
            return myArray; 
        }; 

        const filteredColorShuffled = shuffle(filteredColorCopy);

        console.log('Poke filtrados por cor shuffled:', filteredColorShuffled);

        for (let pokemonName in filteredColorShuffled) {
            let currentName = filteredColorShuffled[pokemonName].name

        // for (let pokemonName in filteredColor) {
        //     let currentName = filteredColor[pokemonName].name
            let newIndex = props.evolutions.findIndex(group => group.first === currentName || group.second === currentName || group.third === currentName );
            console.log('currentName', currentName)
            console.log('índice da evolução', newIndex)
            console.log('pokemonIndex', pokemonName)
            console.log('currentPokemonInfo', filteredColorShuffled[pokemonName])
            // colocar numeros aleatórios para dar chance de vir pokemons evoluidos tbm

            if (controlArray.includes(props.evolutions[newIndex].first || props.evolutions[newIndex].second || props.evolutions[newIndex].third)) {
                console.log('Já tem uma evolução!')
                continue
            }

            if (finalPokemons.length >= 6) {
                break
            }
            
            finalPokemons.push(filteredColorShuffled[pokemonName])
            controlArray.push(filteredColorShuffled[pokemonName].name)
            console.log('Array de controle:', controlArray)
            console.log('Lista final de pokemon:', finalPokemons)

            // console.log('Nome:' , filteredColor[pokemonName]);
            // console.log('Índice:', newIndex);
            // console.log('Lista final:', finalPokemons);
        }
        controlVariable++
    }
        


    // console.log('filteredType_Habitat:', filteredType_Habitat);
    // console.log('filteredColor:', filteredColor)
	return (
		<section>
                <div className="card-container">
                {finalPokemons.map(pokeAttributes => {
                return <PokeCard key={pokeAttributes.name} name={pokeAttributes.name} image={pokeAttributes.image}/>
                })}
                </div>
		</section>
	)
}

export default PokemonResults;
