    }
    

	return (
        <div className="card-container">
            {pokemons.map(pokemon => {
                return <PokeCard key={pokemon.data.name} name={pokemon.data.name} image={pokemon.data.sprites.other.dream_world.front_default}/>
            })}
        </div>
    );
}

export default Home;
