                for (let i = 1; i <= 6; i++) {
                    endpoints.push(`https://pokeapi.co/api/v2/pokemon/${i}`);
                }
    
                const result = await axios.all(endpoints.map(endpoint => axios.get(endpoint)));
                setPokemons(result);
    
                console.log(pokemons)
    
            } catch (error) {
                console.log('An erroroccured:', error);
            }
    
        }

