import { useState } from "react";
import Option from "../Option";

function Question () {

    const colorOptions = ['Black', 'Blue', 'Brown', 'Gray', 'Green', 'Pink', 'Purple', 'Red', 'Yellow', 'White'];
    const transportationOptions = ['Car', 'Airplane', 'Ship', 'Train', 'By foot'];
    const settingOptions = ['Countryside', 'Beach', 'Mountain', 'City', 'Cave'];
    const naturalPhenomenonOptions = ['Volcano', 'Earthquake', 'Tsunami', 'Hurricane', 'Storm', 'Sand storm', 'Blizzard'];



    const [color, setColor] = useState('');
	const [transportation, setTransportation] = useState('');
	const [setting, setSetting] = useState('');
	const [naturalPhenomenon, setNaturalPhenomenon] = useState('');
	const [activity, setActivity] = useState('');
	const [placeToChill, setPlaceToChill] = useState('');
	const [taylorAlbum, setTaylorAlbum] = useState('');
	
	const getSelectionValue = (e) => {
		console.log(e.target.value)

		if (e.target.name === 'color') {
			setColor(e.target.value);
		} else if (e.target.name === 'transportation') {
			setTransportation(e.target.value);
		} else if (e.target.name === 'setting') {
			setSetting(e.target.value);
		} else if (e.target.name === 'naturalPhenomenon') {
			setNaturalPhenomenon(e.target.value);
		} else if (e.target.name === 'activity') {
			setActivity(e.target.value);
		} else if (e.target.name === 'placeToChill') {
			setPlaceToChill(e.target.value);
		} else if (e.target.name === 'taylorAlbum') {
			setTaylorAlbum(e.target.value);
		}
	}

	return (
        <>
            <fieldset>
                <legend>Choose a color:</legend>
                {colorOptions.map(color => <Option key={`option-${color.toLowerCase()}`} id={`option-${color.toLowerCase()}`} name='color' value={color.toLowerCase()} content={color} getData={getSelectionValue}/>)}
            </fieldset>
            <fieldset>
            <legend>Choose a transportation:</legend>
                {transportationOptions.map(transportation => <Option key={`option-${transportation.toLowerCase()}`} id={`option-${transportation.toLowerCase()}`} name='transportation' value={transportation.toLowerCase()} content={transportation} getData={getSelectionValue}/>)}
            </fieldset>
            <fieldset>
                <legend>Choose a setting:</legend>
                {settingOptions.map(setting => <Option key={`option-${setting.toLowerCase()}`} id={`option-${setting.toLowerCase()}`} name='setting' value={setting.toLowerCase()} content={setting} getData={getSelectionValue}/>)}
            </fieldset>
            <fieldset>
                <legend>Choose a natural phenomenon:</legend>
                {naturalPhenomenonOptions.map(phenomenon => <Option key={`option-${phenomenon.toLowerCase().join('-')}`} id={`option-${phenomenon.toLowerCase().join('-')}`} name='naturalPhenomenon' value={phenomenon.toLowerCase().join('')} content={phenomenon} getData={getSelectionValue}/>)}
            </fieldset>
        </>
	)
}

export default Question;
