import axios from "axios";
import { useEffect, useState } from "react";
import PokeCard from "../components/PokeCard";
import './Home.css'

function Home () {

    useEffect(() => {
        getPokemonList();
    }, []) // Para evitar uma re-renderização a cada mudança


    const [pokemons, setPokemons] = useState([]);
    const [pokeAttributes, setPokeAttributes] = useState([]);
    // console.log(pokeAttributes)

    const getPokemonList = async () => {
        // axios.get('https://pokeapi.co/api/v2/pokemon?limit=50')
        // .then(getPokemonList => setPokemons(getPokemonList.data.results))
        // .catch(error => console.log('An error with the following code occured:', error.code));
        
        try {
            let endpoints = [];
        
            for (let i = 1; i <= 6; i++) {
                endpoints.push(`https://pokeapi.co/api/v2/pokemon/${i}`);
            }

            const result = await axios.all(endpoints.map(endpoint => axios.get(endpoint)));
            setPokemons(result);

            console.log(pokemons)

        } catch (error) {
            console.log('An erroroccured:', error);
        }

    }

    useEffect(() => {
        if (pokemons.length > 0) {
            getPokemonAttributes();
        }
    }, [pokemons])

    const getPokemonAttributes = () => {

        const attributes = pokemons.map(pokemon => {
            const pokeData = pokemon.data;
            return {
                pokeID: pokeData.id,
                pokeName: pokeData.name,
                pokeHeight: pokeData.height,
                pokeWeight: pokeData.weight,
                pokeType: pokeData.types[0].type.name
            }
        })

        setPokeAttributes(attributes);
        console.log(pokeAttributes)

    }

    

	return (
        <div className="card-container">
            {pokemons.map(pokemon => {
                return <PokeCard key={pokemon.data.name} name={pokemon.data.name} image={pokemon.data.sprites.other.dream_world.front_default}/>
            })}
        </div>
    );
}

export default Home;
