// import './WelcomeMessage.css'
import { useState } from 'react';
import MoreInfo from "../MoreInfo";
import './MoreInfoLink.css'

function MoreInfoLink () {

    const [isMoreInfo, setIsMoreInfo] = useState(false);

    const toggleMoreInfo = (e) => {

        if (e.key !== "Enter" && e.key !== ' ' && e.type !== 'click') {
            return
        }

        isMoreInfo ? setIsMoreInfo(false) : setIsMoreInfo(true);

    };

	return (
        <>
            {isMoreInfo ? (<MoreInfo toggleMoreInfo={toggleMoreInfo} />) : ''}

            <button className='more-info-link' onClick={toggleMoreInfo} onKeyDown={toggleMoreInfo} role='button' aria-label='Click fore more info about this app' tabIndex={0}>
                <img src='images/pokebola-cor.png' alt='Pokebola link to more info' className='pokebola-info'/>
                <span aria-hidden='true'>Learn more about</span><span aria-hidden='true'>this project</span>
               {/* Learn more about this project */}
            </button>
        </>

    );
}

export default MoreInfoLink;
