import axios from "axios";
import { useEffect, useState } from "react";
import PokeCard from "../components/PokeCard";
import './Home.css'

function Home () {

    const [pokemons, setPokemons] = useState([]);
    const [colors, setColors] = useState([])
    const [pokeAttributes, setPokeAttributes] = useState([]);
    
    useEffect(() => {
        getPokemonList();
    }, []) // Para evitar uma re-renderização a cada mudança

    const getPokemonList = async () => {
        // axios.get('https://pokeapi.co/api/v2/pokemon?limit=50')
        // .then(getPokemonList => setPokemons(getPokemonList.data.results))
        // .catch(error => console.log('An error with the following code occured:', error.code));
        
        try {
            let endpointGeneral = [];
            let endpointColors = [];
        
            for (let i = 1; i <= 6; i++) {
                endpointGeneral.push(`https://pokeapi.co/api/v2/pokemon/${i}`);
                endpointColors.push(`https://pokeapi.co/api/v2/pokemon-species/${i}`);
            }

            const pokeGeneral = await axios.all(endpointGeneral.map(endpoint => axios.get(endpoint)));
            const pokeColors = await axios.all(endpointColors.map(endpoint => axios.get(endpoint)));
            setPokemons(pokeGeneral);

            let arrayOfColors = [];
            for (let i = 0; i < pokeColors.length; i++) {
                arrayOfColors.push(pokeColors[i])
                // console.log(arrayOfColors)
            }
            // setColors(arrayOfColors);
            setColors(arrayOfColors);
            
        } catch (error) {
            console.log('An erroroccured:', error);
        }
        
    }
    
    // console.log(colors[0].data.color.name)
    console.log(colors)

    useEffect(() => {
        if (pokemons.length > 0) {
            getPokemonAttributes();
        }
    }, [pokemons])

    const getPokemonAttributes = () => {

        const attributes = pokemons.map(pokemon => {
            const pokeData = pokemon.data;
            return {
                id: pokeData.id,
                name: pokeData.name,
                height: pokeData.height,
                weight: pokeData.weight,
                type1: pokeData.types[0].type.name,
                type2: pokeData.types.lenght > 1 ? pokeData.types[1].type.name : null,
                image: pokeData.sprites.other.dream_world.front_default,
                color: colors.data.color.name
            }
        })

        setPokeAttributes(attributes);
        
        // console.log(pokeAttributes)
    }
    
    

	return (
        <div className="card-container">
            {pokeAttributes.map(pokeAttributes => {
                return <PokeCard key={pokeAttributes.name} name={pokeAttributes.name} image={pokeAttributes.image}/>
            })}
        </div>
    );
}

export default Home;
