import { useState } from "react";
import { findPokemon } from "../../utils/findPokemon";
import PokeCard from "../PokeCard";
import Pokedex from "../Pokedex";
import "./PokemonResults.css";

// ---------- Finds the final 6 pokemons and render them ---------- //
function PokemonResults (props) {

    const chosenPokemon = findPokemon(props);
    console.log(chosenPokemon);
    let index = 0;
    const [currentPokemon, setCurrentPokemon] = useState(chosenPokemon[index]);
    console.log('currentPokemonBefore', currentPokemon);

    const changePokemon = (e) => {
        const command = e.target.innerText;
        
        if (command === 'Next') {
            if (index === 5) {
               setCurrentPokemon(chosenPokemon[0]);
               console.log('currentPokemonNext',currentPokemon)
               index = 0;
            } else {
                setCurrentPokemon(chosenPokemon[index + 1]);
                console.log('currentPokemonNext',currentPokemon)
                index++
            }
        }

        if (command === 'Prev') {
            if (index === 0) {
               setCurrentPokemon(chosenPokemon[5]);
               console.log('currentPokemonPrev',currentPokemon)
               index = 5;
            } else {
                setCurrentPokemon(chosenPokemon[index - 1]);
                console.log('currentPokemonPrev',currentPokemon)
                index--
            }
        }
    }

	return (
		<section>
            <div className="card-container">
                    <Pokedex changePokemon={changePokemon} leftScreenContent={<img className='pokemon-pokedex' 
                    src={currentPokemon.image}
                    // 
                    />
                    }/>
                 {/* return <PokeCard key={attr.name} name={attr.name} image={attr.image}/> */}
            </div>
		</section>
	)
}

export default PokemonResults;
