import { useEffect, useState } from "react";
import './Home.css'
import { GetAPIData } from "../services/APIData";
import Form from "../components/Form";
import PokemonResults from "../components/PokemonResults";

function Home () {    
    
    // ----- Creating the state for the constants that will be used -----
    const [pokemonData, setPokemonData] = useState([]);
    const [speciesData, setSpeciesData] = useState([]);
    const [evolutionsData, setEvolutionsData] = useState([]);
    const [evolutionsGroups, setEvolutionsGroups] = useState([]);
    const [pokemonAttributes, setPokemonsAttributes] = useState([]);
    const [pokemonResults, setPokemonResults] = useState([]);
    const [answersResults, setAnswersResults] = useState([]);
    
    // ----- On page load -----
    useEffect(() => {
        // ----- Calls the function GetAPIData imported from APIData.jsx to set pokemon, species and evolution data -----
        const fetchData = async () => {
            const APIData = await GetAPIData();

            setPokemonData(APIData.pokemon[0]);
            setSpeciesData(APIData.species[0]);
            setEvolutionsData(APIData.evolutions[0]);
        };

        fetchData();
    }, []);

    // ----- When pokemonData, speciesData or evolutionsData is updated... -----
    useEffect(() => {
        // ----- If the data returned alright... -----
        if (pokemonData.length > 0 && speciesData.length > 0 && evolutionsData.length > 0) {
            // ----- Creates an array with information/attributes of all pokemon -----
            getPokemonAttributes();

            // ----- Creates an array grouping all evolutions of an especies -----
            const pokeEvolutions = evolutionsData.map(groups => {
                const infoChain = groups.data.chain;
                return {
                        first: infoChain.species.name ? infoChain.species.name : null,
                        second: infoChain.evolves_to.length > 0 ? infoChain.evolves_to[0].species.name : null,
                        third: infoChain.evolves_to.length > 0 && infoChain.evolves_to[0].evolves_to.length > 0 ? infoChain.evolves_to[0].evolves_to[0].species.name : null
                    }
                
            })
            setEvolutionsGroups(pokeEvolutions);
        } else {
            console.log('No data available yet.');
        }
    }, [pokemonData, speciesData, evolutionsData]);

    const getPokemonAttributes = () => {

        const pokemonInfo = pokemonData.map((data) => {
            const pokemonData = data.data;
            return {
                id: pokemonData.id,
                name: pokemonData.name,
                height: pokemonData.height,
                weight: pokemonData.weight,
                type1: pokemonData.types[0].type.name,
                type2: pokemonData.types[1] ? pokemonData.types[1].type.name : null,
                image: pokemonData.sprites.other.dream_world.front_default,
            }
        })

        const speciesInfo = speciesData.map(data => {
            const speciesData = data.data;
            return {
                color: speciesData.color.name,
                habitat: speciesData.habitat.name
            }
        })

        const attributes = pokemonInfo.map((pokemonItem, index) => {
            const speciesItem = speciesInfo[index] || {};
            return {
                ...pokemonItem,
                ...speciesItem,
            }
        })

        setPokemonsAttributes(attributes);

    }

    const getFormResults = (pokemonsResults, answersResults) => {
        setPokemonResults(pokemonsResults);
        setAnswersResults(answersResults)
    }

	return (
        <>
        {/* <div className="card-container">
            {pokemonAttributes.map(pokepokemonAttributes => {
                return <PokeCard key={pokepokemonAttributes.name} name={pokepokemonAttributes.name} image={pokepokemonAttributes.image}/>
            })}
        </div> */}
        {pokemonResults.length > 0 ? <PokemonResults pokemonResults={pokemonResults} pokemonAttributes={pokemonAttributes} evolutions={evolutionsGroups} answersResults={answersResults} /> : <Form getFormResults={getFormResults} />}
        </>
    );
}

export default Home;
